(()=>{const e=document.querySelector("#location"),t=document.querySelector(".submit"),n=document.querySelector(".weather-info");t.addEventListener("click",(t=>{t.preventDefault(),async function(e){try{const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=bc268a30fc568797b9cf81bb754ba0f4`,{mode:"cors"});return function(e){const{main:t,description:n}=e.weather[0],{country:a}=e.sys,{name:i,main:r}=e;return{name:i,country:a,main:t,description:n,weatherData:r}}(await t.json())}catch(e){return e}}(e.value).then((e=>{!function(e){const t=`\n    <p>${e.name}, ${e.country}</p>\n      <p>${e.description}</p>\n      <ul>\n        <li>Temperature: ${Math.ceil(e.weatherData.temp-273.15)}&#8451</li>\n        <li>Feels like: ${Math.ceil(e.weatherData.feels_like-273.15)}&#8451</li>\n        <li>Humidity: ${e.weatherData.humidity}%</li>\n      </ul>`;n.innerHTML=t}(e)}))}))})();