(()=>{const e=document.querySelector("#location"),t=document.querySelector(".submit"),a=document.querySelector(".weather-info");t.addEventListener("click",(t=>{t.preventDefault(),async function(e){try{const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=bc268a30fc568797b9cf81bb754ba0f4`,{mode:"cors"});return function(e){const{main:t,description:a}=e.weather[0],{country:n}=e.sys,{name:i,main:c}=e;return{name:i,country:n,main:t,description:a,weatherData:c}}(await t.json())}catch(e){return e}}(e.value).then((e=>{!function(e){const t=`<p class="title">${e.name}, ${e.country}</p>\n  <p class="description">${e.description}</p>\n  <ul>\n    <li class="temps">Temperature: ${Math.ceil(e.weatherData.temp-273.15)}&#8451</li>\n    <li class="temps">Feels like: ${Math.ceil(e.weatherData.feels_like-273.15)}&#8451</li>\n    <li class="temps">Humidity: ${e.weatherData.humidity}%</li>\n  </ul>`;a.innerHTML=t}(e)}))}))})();